# http://rosalind.info/problems/subs/

dna_str = ""
dna_substr = "GGGTAATGG"
index = 0
dna_size = len(dna_str)
substr_size = len(dna_substr)
positions = []
while index < dna_size:
    if dna_str[index : index + substr_size] == dna_substr:
        positions.append(index+1)
    index += 1
print(positions)